import { useState } from "react";
import "./form.css";

export const Form = () => {
  const [formdatas, setDatas] = useState([]);
  const [formulario, setFormulario] = useState({
    nombre: "",
    correo: "",
    edad: "",
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formulario);
    setDatas([...formdatas, formulario]);
    setFormulario({ nombre: "", correo: "", edad: "" });
  };

  const truncar = (username) => {
    if (username.length > 9) {
      return `${username.slice(0, 7)}...`;
    } else {
      return `${username}`;
    }
  };

  const handleDelete = (form) => {
    setDatas(formdatas.filter((form) => form.id !== form));
  };

  return (
    <>
      <main id="mains">
        <header id="agregar">
          <h1>agregar</h1>
        </header>
        <form action="" onSubmit={handleSubmit}>
          <ul id="formulario">
            <ul>
              <li>correo</li>
              <input
                required
                type="text"
                value={formulario.correo}
                placeholder=" Escribir correo.."
                onChange={(e) => {
                  setFormulario({ ...formulario, correo: e.target.value });
                }}
              />
            </ul>

            <ul>
              <li>nombre</li>
              <input
                required
                type="text"
                placeholder=" Escribir Nombre.."
                value={formulario.nombre}
                onChange={(e) => {
                  setFormulario({ ...formulario, nombre: e.target.value });
                }}
              />
            </ul>

            <ul>
              <li>edad</li>
              <input
                required
                type="number"
                placeholder=" Escribir edad.."
                value={formulario.edad}
                onChange={(e) => {
                  setFormulario({ ...formulario, edad: e.target.value });
                }}
              />
            </ul>
            <button id="boton">Send</button>
          </ul>
        </form>
        <table id="result">
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Edad</th>
              <th>Correo</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {formdatas.map((form, index) => (
              <tr key={index}>
                <td>{form.nombre ? truncar(form.nombre) : ""}</td>
                <td>{form.edad}</td>
                <td>{form.correo}</td>
                <td>
                  <button onClick={() => handleDelete(form)}>Eliminar</button>
                  <button>Modificar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </main>
    </>
  );
};
