import { useState } from "react";
import "./form.css";

export const Form = () => {
  const [nombre, setNombre] = useState("");
  const [correo, setCorreo] = useState("");
  const [edad, setEdad] = useState("");
  const [formulario, setFormulario] = useState({
    nombre: "",
    correo: "",
    edad: "",
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormulario({
      nombre,
      correo,
      edad,
    });
    console.log(formulario);
  };
  return (
    <>
      <main id="mains">
        <header id="agregar">
          <h1>agregar</h1>
        </header>
        <form action="" onSubmit={handleSubmit}>
          <ul id="formulario">
            <ul>
              <li>correo</li>
              <input
                type="text"
                placeholder=" Escribir correo.."
                onChange={(e) => {
                  setCorreo(...formulario, e.target.value);
                }}
              />
            </ul>

            <ul>
              <li>nombre</li>
              <input
                type="text"
                placeholder=" Escribir Nombre.."
                onChange={(e) => {
                  setNombre(...formulario, e.target.value);
                }}
              />
            </ul>

            <ul>
              <li>edad</li>
              <input
                type="text"
                placeholder=" Escribir edad.."
                onChange={(e) => {
                  setEdad(...formulario, e.target.value);
                }}
              />
            </ul>
            <button id="boton">Send</button>
          </ul>
        </form>
      </main>
    </>
  );
};
